import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar, Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend);

function App() {
  const [quartos, setQuartos] = useState([]);
  const [taxaOcupacaoTotal, setTaxaOcupacaoTotal] = useState(0);
  const [tempoMedio, setTempoMedio] = useState([]);
  const [historico, setHistorico] = useState([]);
  const [historicoPaciente, setHistoricoPaciente] = useState([]);
  const [nomePaciente, setNomePaciente] = useState('');
  const [nomeMedico, setNomeMedico] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async (paciente = '', medico = '') => {
    try {
      const response = await axios.get('http://localhost:8000/api/', {
        params: { paciente_nome: paciente, medico_nome: medico }
      });

      setQuartos(response.data.quartos);
      setTaxaOcupacaoTotal(response.data.taxaOcupacaoTotal);
      setTempoMedio(response.data.tempoMedioInternacao);
      setHistorico(response.data.historico);
      setHistoricoPaciente(response.data.historicoPaciente);
    } catch (error) {
      console.error('Erro ao carregar dados:', error);
    }
  };

  const handleFilter = (e) => {
    e.preventDefault();
    fetchData(nomePaciente, nomeMedico);
  };


  const ocupacaoData = {
    labels: quartos.map(q => q.tipo),
    datasets: [
      {
        label: 'Ocupados',
        data: quartos.map(q => q.ocupados),
        backgroundColor: 'rgba(255,99,132,0.7)',
      },
      {
        label: 'Disponíveis',
        data: quartos.map(q => q.total - q.ocupados),
        backgroundColor: 'rgba(75,192,192,0.7)',
      }
    ]
  };

  const tempoData = {
    labels: tempoMedio.map(t => t.especialidade),
    datasets: [
      {
        label: 'Tempo médio (dias)',
        data: tempoMedio.map(t => parseFloat(t.media_dias.toFixed(2))),
        backgroundColor: 'rgba(54,162,235,0.7)',
      }
    ]
  };

  const historicoData = {
    labels: historico.map(h => h.mes),
    datasets: [
      {
        label: 'Internações',
        data: historico.map(h => h.internacoes),
        borderColor: 'rgba(255,99,132,1)',
        backgroundColor: 'rgba(255,99,132,0.2)',
        tension: 0.3,
        type: 'line',
      },
      {
        label: 'Altas',
        data: historico.map(h => h.altas),
        borderColor: 'rgba(75,192,192,1)',
        backgroundColor: 'rgba(75,192,192,0.2)',
        tension: 0.3,
        type: 'line',
      }
    ]
  };

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h1 style={{ textAlign: 'center' }}>Dashboard Hospitalar</h1>

      <div style={{ margin: '20px 0', textAlign: 'center' }}>
        <h2>Taxa de Ocupação Total: {taxaOcupacaoTotal}%</h2>
        <Bar data={ocupacaoData} />
      </div>

      <div style={{ margin: '20px 0' }}>
        <h2>Tempo Médio de Internação por Especialidade</h2>
        <Bar data={tempoData} />
      </div>

      <div style={{ margin: '20px 0' }}>
        <h2>Histórico Últimos Meses</h2>
        <Line data={historicoData} />
      </div>

      <div style={{ margin: '20px 0' }}>
        <h2>Histórico do Paciente / Médico</h2>
        <form onSubmit={handleFilter} style={{ marginBottom: '10px' }}>
          <input
            type="text"
            placeholder="Nome do paciente"
            value={nomePaciente}
            onChange={e => setNomePaciente(e.target.value)}
            style={{ marginRight: '10px', padding: '5px' }}
          />
          <input
            type="text"
            placeholder="Nome do médico"
            value={nomeMedico}
            onChange={e => setNomeMedico(e.target.value)}
            style={{ marginRight: '10px', padding: '5px' }}
          />
          <button type="submit" style={{ padding: '5px 10px' }}>Filtrar</button>
        </form>

        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
          <thead>
            <tr style={{ background: '#eee' }}>
              <th style={{ border: '1px solid #333', padding: '5px' }}>Paciente</th>
              <th style={{ border: '1px solid #333', padding: '5px' }}>Tipo</th>
              <th style={{ border: '1px solid #333', padding: '5px' }}>Data</th>
              <th style={{ border: '1px solid #333', padding: '5px' }}>Médico</th>
              <th style={{ border: '1px solid #333', padding: '5px' }}>Diagnóstico</th>
              <th style={{ border: '1px solid #333', padding: '5px' }}>Observações</th>
            </tr>
          </thead>
          <tbody>
            {historicoPaciente.length === 0 ? (
              <tr>
                <td colSpan="6" style={{ textAlign: 'center' }}>Não há histórico com os filtros informados.</td>
              </tr>
            ) : (
              historicoPaciente.map((h, idx) => (
                <tr key={idx}>
                  <td>{h.paciente}</td>
                  <td>{h.tipo}</td>
                  <td>{new Date(h.data).toLocaleString()}</td>
                  <td>{h.medico}</td>
                  <td>{h.diagnostico}</td>
                  <td>{h.observacoes}</td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
}

export default App;
