import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Tooltip,
  Legend
);

export default function Dashboard() {
  const [data, setData] = useState(null);
  const [pacienteNome, setPacienteNome] = useState("");
  const [medicoNome, setMedicoNome] = useState("");

  const carregarDados = async () => {
    const res = await axios.get("http://localhost:8000/api/ocupacao", {
      params: {
        paciente_nome: pacienteNome,
        medico_nome: medicoNome
      }
    });
    setData(res.data);
  };

  useEffect(() => {
    carregarDados();
  }, []);

  if (!data) return <p style={{ textAlign: "center" }}>Carregando...</p>;

  /* ================== GRÁFICOS ================== */
  const tempoData = {
    labels: data.tempoMedioInternacao.map(t => t.especialidade),
    datasets: [
      {
        label: "Tempo médio de internação (dias)",
        data: data.tempoMedioInternacao.map(t => Number(t.media_dias)),
        backgroundColor: "rgba(54, 162, 235, 0.7)"
      }
    ]
  };

  const historicoData = {
    labels: data.historico.map(h => h.mes),
    datasets: [
      {
        label: "Internações",
        data: data.historico.map(h => h.internacoes),
        borderColor: "#e53935",
        backgroundColor: "rgba(229,57,53,0.2)",
        fill: true
      },
      {
        label: "Altas",
        data: data.historico.map(h => h.altas),
        borderColor: "#43a047",
        backgroundColor: "rgba(67,160,71,0.2)",
        fill: true
      }
    ]
  };

  return (
    <div style={{ maxWidth: 1100, margin: "auto", padding: 20 }}>
      <h1 style={{ textAlign: "center" }}>Dashboard Hospitalar</h1>

      <h2 style={{ textAlign: "center" }}>
        Taxa de Ocupação Total: {data.taxaOcupacaoTotal}%
      </h2>

      {/* FILTROS */}
      <div style={{ display: "flex", gap: 10, justifyContent: "center", margin: 30 }}>
        <input
          placeholder="Paciente"
          value={pacienteNome}
          onChange={e => setPacienteNome(e.target.value)}
        />
        <input
          placeholder="Médico"
          value={medicoNome}
          onChange={e => setMedicoNome(e.target.value)}
        />
        <button onClick={carregarDados}>Buscar</button>
      </div>

      {/* TEMPO MÉDIO */}
      <div style={{ height: 300 }}>
        <h3 style={{ textAlign: "center" }}>
          Tempo Médio de Internação (dias)
        </h3>
        <Bar data={tempoData} />
      </div>

      {/* HISTÓRICO */}
      <div style={{ height: 300, marginTop: 50 }}>
        <h3 style={{ textAlign: "center" }}>
          Internações vs Altas
        </h3>
        <Line data={historicoData} />
      </div>
    </div>
  );
}
