import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar, Line } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend } from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, PointElement, LineElement, Title, Tooltip, Legend);

export default function Dashboard() {
  const [data, setData] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:8000/api/ocupacao")
      .then(res => setData(res.data))
      .catch(err => console.error(err));
  }, []);

  if (!data) return <p>Carregando...</p>;

  // Preparar dados para os gráficos
  const ocupacaoData = {
    labels: data.quartos.map(q => q.tipo),
    datasets: [
      { label: "Ocupados", data: data.quartos.map(q => q.ocupados), backgroundColor: "rgba(255,99,132,0.7)" },
      { label: "Disponíveis", data: data.quartos.map(q => q.total - q.ocupados), backgroundColor: "rgba(75,192,192,0.7)" }
    ]
  };

  const tempoData = {
    labels: data.tempoMedioInternacao.map(t => t.especialidade),
    datasets: [
      { label: "Tempo médio (dias)", data: data.tempoMedioInternacao.map(t => parseFloat(t.media_dias).toFixed(2)), backgroundColor: "rgba(54,162,235,0.7)" }
    ]
  };

  const historicoData = {
    labels: data.historico.map(h => h.mes),
    datasets: [
      { label: "Internações", data: data.historico.map(h => h.internacoes), borderColor: "rgba(255,99,132,1)", backgroundColor: "rgba(255,99,132,0.2)", tension: 0.3, fill: true },
      { label: "Altas", data: data.historico.map(h => h.altas), borderColor: "rgba(75,192,192,1)", backgroundColor: "rgba(75,192,192,0.2)", tension: 0.3, fill: true }
    ]
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>Dashboard Hospitalar</h1>
      <h2>Taxa de Ocupação Total: {data.taxaOcupacaoTotal}%</h2>

      <div style={{ marginBottom: "40px" }}>
        <h3>Ocupação por Tipo de Quarto</h3>
        <Bar data={ocupacaoData} options={{ responsive: true, plugins: { legend: { position: "top" } } }} />
      </div>

      <div style={{ marginBottom: "40px" }}>
        <h3>Tempo Médio de Internação por Especialidade</h3>
        <Bar data={tempoData} options={{ responsive: true }} />
      </div>

      <div style={{ marginBottom: "40px" }}>
        <h3>Histórico Últimos 6 Meses</h3>
        <Line data={historicoData} options={{ responsive: true }} />
      </div>
    </div>
  );
}
