import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar, Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

export default function Dashboard() {
  const [data, setData] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:8000/api/ocupacao")
      .then(res => setData(res.data))
      .catch(err => console.error(err));
  }, []);

  if (!data) return <p style={{ textAlign: "center" }}>Carregando...</p>;


  const ocupacaoData = {
    labels: data.quartos.map(q => q.tipo),
    datasets: [
      {
        label: "Ocupados",
        data: data.quartos.map(q => q.ocupados)
      },
      {
        label: "Disponíveis",
        data: data.quartos.map(q => q.total - q.ocupados)
      }
    ]
  };

  const tempoData = {
    labels: data.tempoMedioInternacao.map(t => t.especialidade),
    datasets: [
      {
        label: "Tempo médio (dias)",
        data: data.tempoMedioInternacao.map(t =>
          Number(parseFloat(t.media_dias).toFixed(2))
        )
      }
    ]
  };

  const historicoData = {
    labels: data.historico.map(h => h.mes),
    datasets: [
      {
        label: "Internações",
        data: data.historico.map(h => h.internacoes),
        tension: 0.3,
        fill: true
      },
      {
        label: "Altas",
        data: data.historico.map(h => h.altas),
        tension: 0.3,
        fill: true
      }
    ]
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { position: "top" }
    }
  };


  return (
    <div style={{ padding: "20px" }}>
      <h1 style={{ textAlign: "center" }}>Dashboard Hospitalar</h1>
      <h3 style={{ textAlign: "center", marginBottom: "40px" }}>
        Taxa de Ocupação Total: {data.taxaOcupacaoTotal}%
      </h3>

      <div style={containerStyle}>
        <div style={cardStyle}>
          <h4>Ocupação por Tipo de Quarto</h4>
          <div style={chartBox}>
            <Bar data={ocupacaoData} options={chartOptions} />
          </div>
        </div>

        <div style={cardStyle}>
          <h4>Tempo Médio de Internação</h4>
          <div style={chartBox}>
            <Bar data={tempoData} options={chartOptions} />
          </div>
        </div>

        <div style={cardStyle}>
          <h4>Histórico de Internações x Altas</h4>
          <div style={chartBox}>
            <Line data={historicoData} options={chartOptions} />
          </div>
        </div>
      </div>
    </div>
  );
}


const containerStyle = {
  maxWidth: "900px",
  margin: "0 auto",
  display: "flex",
  flexDirection: "column",
  gap: "40px"
};

const cardStyle = {
  background: "#fff",
  padding: "20px",
  borderRadius: "10px",
  boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
  textAlign: "center"
};

const chartBox = {
  height: "280px",
  marginTop: "20px"
};
