import React, { useEffect, useState } from "react";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Tooltip, Legend);

export default function FarmaciaDashboard() {
  const [dados, setDados] = useState({
    estoqueBaixo: [],
    proximosVencimentos: [],
    curvaConsumo: []
  });

  useEffect(() => {
    axios.get("http://localhost:8000/api/farmacia")
      .then(res => setDados(res.data))
      .catch(err => console.error(err));
  }, []);

  const curvaConsumoData = {
    labels: dados.curvaConsumo.map(m => m.nome),
    datasets: [
      {
        label: "Prescrições",
        data: dados.curvaConsumo.map(m => m.quantidade_prescrita),
        backgroundColor: "#1976d2"
      },
      {
        label: "Compras",
        data: dados.curvaConsumo.map(m => m.quantidade_comprada),
        backgroundColor: "#388e3c"
      }
    ]
  };

  return (
    <div style={{ padding: 20, maxWidth: 1100, margin: "auto" }}>
      <h1 style={{ textAlign: "center" }}>Dashboard Farmácia</h1>

      <h3>Medicamentos com estoque baixo</h3>
      <table width="100%" border="1" cellPadding="8">
        <thead>
          <tr>
            <th>Nome</th>
            <th>Estoque</th>
            <th>Estoque Mínimo</th>
          </tr>
        </thead>
        <tbody>
          {dados.estoqueBaixo.map(m => (
            <tr key={m.id}>
              <td>{m.nome}</td>
              <td>{m.estoque}</td>
              <td>{m.estoque_minimo}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3 style={{ marginTop: 30 }}>Medicamentos próximos do vencimento</h3>
      <table width="100%" border="1" cellPadding="8">
        <thead>
          <tr>
            <th>Nome</th>
            <th>Data de Vencimento</th>
            <th>Dias Restantes</th>
          </tr>
        </thead>
        <tbody>
          {dados.proximosVencimentos.map(m => (
            <tr key={m.id}>
              <td>{m.nome}</td>
              <td>{m.data_validade}</td>
              <td>{m.dias_restantes}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3 style={{ marginTop: 30 }}>Curva de Consumo (Últimos 3 meses)</h3>
      <div style={{ height: 300 }}>
        <Bar
          data={curvaConsumoData}
          options={{ responsive: true, plugins: { legend: { position: "top" } } }}
        />
      </div>
    </div>
  );
}
